INSERT INTO user_profiles (user_id, version, agreed_terms_version, agreed_terms_date, email_verified, email_verification_key, forgot_key, forgot_key_date, self_banned_date, client_verified, login_attempt_first_date, login_attempt_cnt)
				  SELECT u.id, coalesce(la.version, 0), u.agreed_terms_version, u.agreed_terms_date, coalesce(u.email_verified, false), u.email_verification_key, u.forgot_key, u.forgot_key_date, u.self_banned_date, u.client_verified, la.date_created, coalesce(la.attempts, 0)
					FROM users u LEFT JOIN user_login_attempts la on upper(u.username) = upper(la.username);

create table int_message ( message_id char(36), region varchar(100), created_date datetime not null, message_bytes blob, constraint message_pk primary key (message_id, region) );
create index int_message_ix1 on int_message (created_date);
create table int_group_to_message ( group_key char(36), message_id char(36), region varchar(100), constraint group_to_messag_pk primary key (group_key, message_id, region) );
create table int_message_group ( group_key char(36), region varchar(100), marked bigint, complete bigint, last_released_sequence bigint, created_date datetime not null, updated_date datetime default null, constraint message_group_pk primary key (group_key, region) );

